import axios from "axios";
import { useEffect, useState } from "react";
import AlertBox from "./Companent/Alert";
import LoadingSpinner from "./Companent/Spinner/Spinner"
import "./index.css"



function App() {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState(false);

  useEffect(() => {
    setIsLoading(true)
    const fetchData = async () => {
      const response = await axios.get("https://restcountries.com/v2/all");
      setData(response.data)
      setIsLoading(false)
    }
    fetchData();
  }, []);

  function getData(key) {
    if (key === ""){
    console.log("12")
    }
  }

  const searchData = async (e) => {
    setErrorMessage(false)
    try {
      const response = await axios.get("https://restcountries.com/v2/capital/" + e);
      setData(response.data)
    } catch (err) {
      setErrorMessage(true);
      setIsLoading(false);
    }
  }

  const countryList = (
    <div className="container">
      <input className="search-bar" type="search" placeholder="Search" onChange={e => searchData(e.target.value)} />
      <table className="table">
        <thead>
          <tr>
            <th scope="col">img</th>
            <th scope="col">Name</th>
            <th scope="col">Capital</th>
            <th scope="col">Region</th>
          </tr>
        </thead>
        {data.map(repo =>
          <tbody key={repo.name}>
            <tr>
              <td><img src={repo.flag} style={{ width: "200px" }} alt="" /></td>
              <td>{repo.name}</td>
              <td>{repo.capital}</td>
              <td>{repo.region}</td>
            </tr>
          </tbody>)}
      </table>
    </div>
  )

  return (
    <div className="App">
      {errorMessage ? <AlertBox/> : null}
        {isLoading ? <LoadingSpinner /> : countryList}
    </div>

  );
}

export default App;
